import React, { useEffect, useState } from 'react'

// https://dev.to/dom_the_dev/how-to-use-the-spotify-api-in-your-react-js-app-50pn#authentication

export default function Home() {
  // This is the query sent by the client to the Spotify authentication server.
  const query =
  {
    "client_id": "edcd5a7d1ed6481ebf796b856adaefcf",
    "auth_endpoint": "https://accounts.spotify.com/authorize",
    "response_type": "token",
    "redirect_uri": "http://localhost:3000/",
    "scope": "user-follow-read",
    "show_dialog": "true"
  }


  // When the user logs into Spotify, Spotify will return a URL that contains a token.
  // We need to extract this access token from the URL. Below is a helper function to achieve that.
  const getSpotifyAuthResponse = (hash) => {
    // Example URL - localhost:3000/#access_token=BQDvbd1UySh2eL4&token_type=Bearer&expires_in=3600
    const strAfterHashtag = hash.substring(1);
    const paramsInUrl = strAfterHashtag.split("&");

    // This accumulater should return three values - token, expires_in and the token type.
    const paramsSplitUp = paramsInUrl.reduce((accumulater, currentValue) => {
      console.log("Debug: ", currentValue)
      const [key, value] = currentValue.split("=");
      accumulater[key] = value;
      return accumulater;
    }, {})

    return paramsSplitUp;
  }


  // Code within useEffect will execute whenever the page is re-rendered.
  useEffect( () => {
    // Run if the browser url has some data within it (e.g. URL recieved from spotify auth)
    if(window.location.hash) {
      // Object destructuring. Function is called and the returned variables are stored.
      // Note that tokens are valid for 1 hour. 
      const {access_token, expires_in, token_type} = getSpotifyAuthResponse(window.location.hash);

      localStorage.clear();
      localStorage.setItem("accessToken", access_token);
      localStorage.setItem("tokenType", token_type);
      localStorage.setItem("expiresIn", expires_in);
    }
  })


  return (
    <div>
      <h1>Homepage</h1>
      <a href=
        {`${query.auth_endpoint}?client_id=${query.client_id}&redirect_uri=${query.redirect_uri}&response_type=${query.response_type}&scope=${query.scope}`}
      >Login to Spotify</a>

      <br></br>
      {/* <p onClick={testAPI}>Click to trigger test API</p> */}
    </div>
  )
}


